SOLIC_IMPUGNACION SET Language 'Spanish';    select  proceso.expediente,      proceso.nombre_infractor,      (select CONCAT('Ab. ', Upper(Concat (pf.nombre1, ' ', pf.nombre2, ' ', pf.apellido1, ' ', pf.apellido2)) )  as nombre   from funcionario f inner join firma_persona fp on fp.id_persona = f.id_persona  inner join persona pf on pf.id_persona= f.id_persona    WHERE f.id_cargo =11  and f.fecha_final_vigencia is null   ) as funcionario,      'DELEGADO DEL DIRECTOR DE GESTION DE INFRACCIONES Y SERVICIOS DE TRANSITO' as CARGO_DELEGADO,      delegado.nombre,      (select im.numero_imagen from imagen im where im.id_tipo_imagen = 1 and fecha_fin is null) logoATM,      delegado.firmaAgente,      CONVERT(VARCHAR,DAY(getdate())) + ' de ' +       CONVERT(VARCHAR,DATENAME(MONTH, GETDATE())) + ' de ' +       CONVERT(VARCHAR,YEAR(GETDATE())) + ', a las ' +       CONVERT(VARCHAR,DATEPART(hour, getdate())) + 'h' +       CONVERT(VARCHAR,DATEPART(minute, getdate())) as fechaSolicitud,        (SELECT top 1 mi.documento_faltante FROM trazabilidad_proceso trz INNER JOIN motivacion_impugnacion mi on mi.id_trazabilidad_proceso = trz.id_trazabilidad_proceso  WHERE trz.id_proceso= proceso.id_proceso order by fecha_inicio DESC) desc_motivacion    from   (      select pr.id_proceso,       convert(varchar,pr.numero_proceso) +' - '+ convert(varchar,year(pr.fecha_inicio)) as expediente,       CASE pe.id_tipo_identificacion WHEN '2' THEN pj.nombre_comercial ELSE Upper(Concat (pe.nombre1, ' ', pe.nombre2, ' ', pe.apellido1, ' ', pe.apellido2)) END as nombre_infractor    from  proceso pr (nolock) inner join      participante_proceso ppr (nolock) on ppr.id_proceso = pr.id_proceso left join      persona pe  (nolock) on pe.id_persona = ppr.id_persona left join      persona_juridica pj (nolock)                 ON pj.id_persona_juridica = pe.id_persona     where pr.id_proceso is not null  ) proceso ,    (SELECT     top 1 fa.numero_imagen firmaAgente,       Upper(Concat (nombre1, ' ', nombre2, ' ', apellido1, ' ', apellido2))  as nombre  FROM    PERSONA p LEFT JOIN   firma_persona fa on fa.id_persona = p.id_persona  WHERE p.ID_PERSONA IN (select  TOP 1 ID_PERSONA from funcionario where id_cargo = 6 and fecha_final_vigencia is null  ORDER BY NEWID() )  order by fa.fecha_registro desc  ) delegado
