ect         top 1 isnull(f.titulo_obtenido, 'Abg.') + Upper(          Concat(            ' ', p.nombre1, ' ', p.nombre2, '  ',             p.apellido1, ' ', p.apellido2          )        )       from         funcionario f         inner join firma_persona fp on fp.id_persona = f.id_persona         inner join persona p on p.id_persona = f.id_persona       WHERE         f.id_cargo = 1         and f.fecha_final_vigencia is null    ) as abogado,     (      select         top 1           referencia_envio      from         envio_doc_impresion_embargo as env         INNER JOIN seguimiento_motivo_levantamiento seg on seg.id_seg_doc_impresion_embargo = env.id_seg_doc_impresion_embargo       where         seg.id_proceso = p.id_proceso       order by    env.id_envio_doc_impresion_embargo desc,         seg.fecha_registro desc    ) as numeroTramite,     (      select         top 1 fp.numero_imagen       from         funcionario f         inner join firma_persona fp on fp.id_persona = f.id_persona         inner join persona p on p.id_persona = f.id_persona       WHERE         f.id_subcargo = 3    ) as firmaAbogado,     (      select         top 1 fp.numero_imagen       from         funcionario f         inner join firma_persona fp on fp.id_persona = f.id_persona         inner join persona p on p.id_persona = f.id_persona       WHERE         f.id_cargo = 1         and f.fecha_final_vigencia is null    ) as firmaAbogado1,     segmt.idCodigoQR as codigoQR,     (      select         numero_imagen       from         imagen       where         id_tipo_imagen in(          SELECT             id_tipo_imagen           FROM             tipo_imagen           WHERE             codigo = 'LOGGO'        )         AND fecha_fin is null    ) logoATM,    ic.cuenta as 'Nro_cuenta'  from     coactivo co     inner join coactivo_proceso cop on cop.id_coactivo = co.id_coactivo    inner join impulso_coactivo ic on co.id_coactivo = ic.id_coactivo    inner join proceso p on p.id_proceso = cop.id_proceso    