AR) AS fechaProceso,         (SELECT img.numero_imagen          FROM   imagen img (nolock)          WHERE  img.id_tipo_imagen = 1                 AND ( ( img.fecha_inicio <= CONVERT(DATE, @FECHA)                         AND img.fecha_fin >= CONVERT(DATE, @FECHA) )                        OR ( img.fecha_inicio <= CONVERT(DATE, @FECHA)                             AND img.fecha_fin IS NULL ) ))      AS logo,         (SELECT Max(fp.numero_imagen)          FROM   funcionario f (nolock)                 INNER JOIN persona per (nolock)                         ON f.id_persona = per.id_persona                 INNER JOIN firma_persona fp (nolock)                         ON fp.id_persona = per.id_persona          WHERE  f.id_cargo = 11                 AND f.fecha_inicio_vigencia <= CONVERT(DATE, @FECHA)                 AND f.fecha_final_vigencia >= CONVERT(DATE, @FECHA)                  OR ( f.id_cargo = 11                       AND f.fecha_inicio_vigencia <= CONVERT(DATE, @FECHA)                       AND f.fecha_final_vigencia IS NULL ))     AS         FIRMA_FUNCIONARIO_,         (SELECT Concat(f.titulo_obtenido, ' ', Upper(                 Concat(per.nombre1, ' ', per.nombre2, ' ',                         per.apellido1, ' ',                 per.apellido2)), ', Mgs')          FROM   funcionario f (nolock)                 INNER JOIN persona per (nolock)                         ON f.id_persona = per.id_persona          WHERE  f.id_cargo = 11                 AND f.fecha_inicio_vigencia <= CONVERT(DATE, @FECHA)                 AND f.fecha_final_vigencia >= CONVERT(DATE, @FECHA)                  OR ( f.id_cargo = 11                       AND f.fecha_inicio_vigencia <= CONVERT(DATE, @FECHA)                       AND f.fecha_final_vigencia IS NULL ))     AS nombreDirector         ,         (SELECT valor_parametro_defecto          FROM   parametro(nolock)          WHERE  codigo_parametro = 436)                         AS cargo_delegado         ,         (SELECT nombre          FROM   cargo(nolock)          WHERE  id_cargo = 11)                                  AS cargo_director  FROM   coactivo c (nolock)         INNER JOIN obligacion_coactivo oc                 ON oc.id_coactivo = c.id_coactivo         JOIN persona pe (nolock)           ON c.id_deudor = pe.id_persona         LEFT JOIN persona_juridica pj (nolock)                ON pj.id_persona_juridica = pe.id_persona         INNER JOIN comparendo com (nolock)                 ON oc.numero_obligacion = com.id_factura_axis         LEFT JOIN agente a (nolock)                ON a.placa_agente_transito = com.placa_agente_transito         LEFT JOIN persona pa (nolock)                ON a.id_persona = pa.id_persona         JOIN proceso p (nolock)           ON p.id_proceso = c.id_proceso  WHERE  c.id_coactivo = :idCoactivo         AND oc.numero_obligacion = :numeroObligacion
