


begin tran
UPDATE documentos..plantilla_configuracion
SET    consulta=   'SET Language ''Spanish'';    select top 1     (SELECT UPPER(nombre) from entidad_bien where id_entidad_bien=:banco) as Banco,     (select top 1 isnull(seg.consecutivo_oficio,'''') from seguimiento_motivo_levantamiento seg where seg.id_proceso = p.id_proceso order by seg.fecha_registro desc ) as numero_consecutivo,     CONCAT(p.numero_proceso,''-'',DATEPART(yy,p.fecha_inicio)) as numeroJuicio,  CONVERT(VARCHAR,DAY(getdate())) + '' de '' +       CONVERT(VARCHAR,DATENAME(MONTH, GETDATE())) + '' del '' +       CONVERT(VARCHAR,YEAR(GETDATE()))  as FECHA_TEXTO,     CONCAT( CONVERT(VARCHAR, DATEPART(hour, getdate())) , '':'' ,     CONVERT(VARCHAR,   CASE WHEN DATEPART(minute, getdate()) > 10  THEN  CONVERT(VARCHAR,DATEPART(minute, getdate())) ELSE CONCAT(''0'',CONVERT(VARCHAR,DATEPART(minute, getdate()))) END ) ) as hora,     case per.id_tipo_identificacion when 2 then UPPER(perj.nombre_comercial) else UPPER(CONCAT(per.nombre1,'' '',per.nombre2,'' '',per.apellido1,'' '', per.apellido2))  end as nombre,  per.numero_identificacion as ''Numero identificacion'',     (select top 1 isnull(f.titulo_obtenido,''Abg.'') + Upper(Concat('' '',p.nombre1, '' '', p.nombre2, ''  '', p.apellido1, '' '', p.apellido2))    from funcionario f inner join firma_persona fp on fp.id_persona = f.id_persona  inner join persona p on p.id_persona= f.id_persona    WHERE f.id_subcargo =3) as abogAsignado,     (select top 1 isnull(f.titulo_obtenido,''Abg.'') + Upper(Concat('' '',p.nombre1, '' '', p.nombre2, ''  '', p.apellido1, '' '', p.apellido2))    from funcionario f inner join firma_persona fp on fp.id_persona = f.id_persona  inner join persona p on p.id_persona= f.id_persona    WHERE f.id_cargo =1 and f.fecha_final_vigencia is null) as abogado,       (select top 1 CONCAT(convert(varchar(10),fecha_envio), '' '', referencia_envio) from envio_doc_impresion_embargo as env  INNER JOIN seguimiento_motivo_levantamiento  seg on seg.id_seg_doc_impresion_embargo =  env.id_seg_doc_impresion_embargo  where seg.id_proceso= p.id_proceso order by seg.fecha_registro desc) as numeroTramite,       (select top 1 fp.numero_imagen   from funcionario f inner join firma_persona fp on fp.id_persona = f.id_persona  inner join persona p on p.id_persona= f.id_persona   WHERE f.id_subcargo =3) as firmaAbogado,     (select top 1 fp.numero_imagen    from funcionario f inner join firma_persona fp on fp.id_persona = f.id_persona  inner join persona p on p.id_persona= f.id_persona    WHERE f.id_cargo = 1 and f.fecha_final_vigencia is null) as firmaAbogado1,     segmt.idCodigoQR as codigoQR,     (select numero_imagen from imagen where id_tipo_imagen in(SELECT id_tipo_imagen FROM tipo_imagen WHERE codigo = ''LOGGO'' ) AND fecha_fin is null) logoATM    from   coactivo co inner join         coactivo_proceso cop on cop.id_coactivo = co.id_coactivo inner join        proceso p on p.id_proceso = cop.id_proceso inner join        trazabilidad_proceso tr on tr.id_proceso = p.id_proceso and tr.id_estado_proceso in(29,36,37,45,52,56,27) inner join        persona per on per.id_persona = co.id_deudor inner join        tipo_identificacion_persona tpid on tpid.id_tipo_identificacion=per.id_tipo_identificacion left join        persona_juridica perj on perj.id_persona_juridica = co.id_deudor inner join        estado_proceso estp on estp.id_estado_proceso = p.id_estado_proceso left join        seguimiento_motivo_levantamiento segmt on segmt.id_proceso = p.id_proceso    where   year(p.fecha_inicio) = :anio    and p.numero_proceso=:numeroProceso'               
	   , orden_variables='BANCO,numero_consecutivo,NUMERO_JUICIO,FECHA_TEXTO,HORA,NOMBRE_COMPLETO,N_DOCUMENTO_INFRACTOR,NOMBRE_ABOGADO_ASIG,NOMBRE_ABOGADO,NUMERO_TRAMITE,FIRMA_FUNCIONARIO_,FIRMA_AGENTE,CODIGO_QR,LogoATM_variable'
WHERE  id_plantilla_config=10133

commit tran
                        --BANCO,numero_consecutivo,NUMERO_JUICIO,FECHA_TEXTO,HORA,NOMBRE_COMPLETO,N_DOCUMENTO_INFRACTOR,NOMBRE_ABOGADO_ASIG,NOMBRE_ABOGADO,NUMERO_TRAMITE,FIRMA_FUNCIONARIO_,FIRMA_AGENTE,CODIGO_QR,LogoATM_variable





