SELECT 'data_documento' AS 'Tabla', A.* FROM gestor_docs..data_documento A WITH(NOLOCK) 
INNER JOIN documentos..jasper_plantilla B WITH(NOLOCK) ON A.codigo_documento = B.codigo_documento
	AND B.id_plantilla = @PLANTILLA_ID

SELECT 'version_data_documento' AS 'Tabla', A.* FROM gestor_docs..version_data_documento A WITH(NOLOCK)
INNER JOIN documentos..jasper_plantilla B WITH(NOLOCK) ON A.codigo_documento = B.codigo_documento
	AND B.id_plantilla = @PLANTILLA_ID
ORDER BY A.[version]

SELECT 'variable_plantilla - variable' AS 'Tabla', A.*, '' '-', B.* FROM documentos..variable_plantilla A WITH(NOLOCK) 
LEFT JOIN documentos..variable B WITH(NOLOCK) ON A.id_variable = B.id_variable
WHERE A.id_plantilla = @PLANTILLA_ID

SELECT 'jasper_plantilla' AS 'Tabla', * FROM documentos..jasper_plantilla A WITH(NOLOCK) WHERE A.id_plantilla = @PLANTILLA_ID
SELECT 'xml_plantilla' AS 'Tabla', * FROM documentos..xml_plantilla A WITH(NOLOCK) WHERE A.id_xml_plantilla = @PLANTILLA_ID
